<?phpclass aulas extends Database {    function get_all_aulas() {        $stmt = 'SELECT                        aulas.id_aula,                        aulas.nombre_aula,                        modulos.nombre_modulo,                        aulas.f_inicio,                        aulas.f_fin,                        aulas.url_aula,                        aulas.imagen_aula                    FROM                            aulas                    LEFT JOIN modulos ON modulos.id_modulo = aulas.fk_modulo                    ORDER BY aulas.f_inicio DESC';        return $this->getRecord($stmt);    }    function get_by_modulo($modulo) {        $stmt = "SELECT                aulas.id_aula,                aulas.nombre_aula,                aulas.f_inicio,                aulas.url_aula,                aulas.fk_modulo,                ediciones.mes_init,                ediciones.anio_init,                servicios.name_servicio                FROM                        aulas                LEFT JOIN aulas_ediciones ON aulas_ediciones.fk_aula = aulas.id_aula                LEFT JOIN ediciones ON aulas_ediciones.fk_edicion = ediciones.id_ediciones                LEFT JOIN servicios ON servicios.id_servicio = ediciones.fk_servicio                WHERE                        aulas.fk_modulo = " . $modulo . "                GROUP BY aulas.id_aula                ORDER BY ediciones.anio_init DESC, ediciones.mes_init DESC";//        echo $stmt;        return $this->getRecord($stmt);    }    function get_aula_by_profe($id) {        $stmt_1 = "";        if ($id != (10 || 3)) {            $stmt_1 = '';        }        $stmt = "SELECT                     aulas.id_aula,                    aulas.nombre_aula,                    modulos.nombre_modulo,                    aulas.f_inicio,                    aulas.f_fin,                    aulas.url_aula,                    aulas.imagen_aula                FROM                    aulas                        JOIN                    modulos ON modulos.id_modulo = aulas.fk_modulo                        LEFT JOIN aulas_profesores ON aulas_profesores.fk_aula = aulas.id_aula                        JOIN people ON people.id_people = aulas_profesores.fk_profesor                WHERE aulas_profesores.fk_profesor = " . $id . "                  GROUP BY aulas.id_aula                ORDER BY aulas.f_inicio DESC";        echo $stmt;        return $this->getRecord($stmt);    }    function get_by_profeymodulo($id_profe, $id_modulo) {              $stmt = "SELECT                    aulas.id_aula,                    aulas.nombre_aula,                    modulos.nombre_modulo,                    modulos.id_modulo,                    aulas.f_inicio,                    aulas.f_fin,                    aulas.url_aula,                    aulas.imagen_aula                FROM                        aulas                JOIN modulos ON modulos.id_modulo = aulas.fk_modulo                LEFT JOIN aulas_profesores ON aulas_profesores.fk_aula = aulas.id_aula                JOIN people ON people.id_people = aulas_profesores.fk_profesor                WHERE                        aulas_profesores.fk_profesor = " . $id_profe . "                AND modulos.id_modulo = " . $id_modulo . "                GROUP BY                        aulas.id_aula                ORDER BY                        aulas.f_inicio DESC";//        echo $stmt;        return $this->getRecord($stmt);    }    function create_aula($data) {//        print_r($data);        $stmt = "INSERT INTO aulas "                . "(fk_modulo, f_inicio, url_aula) "                . "VALUES "                . "('" . $data["fk_modulo"] . "', '" . $data["f_inicio"] . "', '" . $data["url_aula"] . "');";//        echo $stmt;        $this->mysqli->query($stmt);        return $this->mysqli->insert_id;    }    function get_aula_by_id($id) {        $stmt = 'SELECT                aulas.id_aula,                aulas.nombre_aula,                aulas.fk_modulo,                modulos.nombre_modulo,                aulas.f_inicio,                aulas.f_fin,                aulas.url_aula,                aulas.imagen_aula,                ediciones.anio_init,                ediciones.mes_init,                ediciones.fk_servicio,                servicios.name_servicio                FROM                        aulas                LEFT JOIN modulos ON modulos.id_modulo = aulas.fk_modulo                LEFT JOIN aulas_ediciones ON aulas.id_aula = aulas_ediciones.fk_aula                LEFT JOIN ediciones ON aulas_ediciones.fk_edicion = ediciones.id_ediciones                LEFT JOIN servicios ON ediciones.fk_servicio = servicios.id_servicio                WHERE aulas.id_aula = "' . $id . ' "                ORDER BY aulas.nombre_aula ASC                ';//        echo $stmt;        $res = $this->getRecord($stmt);        return $res[0];    }    function get_videos_by_aula($id) {        $stmt = 'SELECT *                FROM aulas as a                LEFT JOIN modulos_orden as m ON a.fk_modulo = m.fk_modulo                LEFT JOIN servicos_datos as s ON m.fk_servicio_educativo = s.fk_servicios                WHERE a.id_aula = ' . $id . '                 GROUP BY s.video_servicios';        return $this->getRecord($stmt);    }    function remove_aula($aula) {        $stmt = "DELETE FROM aulas "                . "WHERE id_aula = '"                . $aula . "'";//        echo $stmt;        return $this->mysqli->query($stmt);    }    function update_aula($data, $aula) {//        print_r($data);        $stmt = "UPDATE aulas "                . "SET fk_modulo='" . $data["fk_modulo"] . "', "                . "f_inicio='" . $data["f_inicio"] . "', "                . "url_aula='" . $data["url_aula"] . "' "                . "WHERE id_aula = '" . $aula . "'";//        echo $stmt;        return $this->mysqli->query($stmt);    }    function set_image_aula($data, $id) {//        print_r($data);        $name = saveFile($data["name"][0], $data["tmp_name"][0], '/images/aulas/', false, true, true);        $stmt = "UPDATE aulas "                . "SET imagen_aula = '" . $name . "' "                . "WHERE id_aula = '" . $id . "'";        return $this->mysqli->query($stmt);    }}