<?phpclass aulas_ediciones extends Database {    function conect($aula, $data) {        $out = '';        $dataadd = '';        if (count($data) > 0) {            $i = 0;            foreach ($data as $edicion) {                if ($i >= 1) {                    $dataadd .= ", ";                }                $dataadd .= "('" . $edicion . "', '" . $aula . "') ";                $i++;            }            $stmt = "INSERT INTO "                    . "aulas_ediciones (fk_edicion, fk_aula) "                    . "VALUES " . $dataadd;            $out = $this->saveRecord($stmt);        } else {            $out = false;        }//        echo $stmt;        return $out;    }    function get_by_aula($aula) {        $stmt = "SELECT fk_edicion FROM aulas_ediciones WHERE fk_aula = '" . $aula . "'";//        echo $stmt;        return $this->getRecord($stmt);    }    function remove($aula, $edicion) {        $stmt = "DELETE FROM aulas_ediciones "                . "WHERE fk_aula = '" . $aula . "' "                . "AND fk_edicion = '" . $edicion . "'";        return $this->mysqli->query($stmt);    }    function remove_by($aula) {        $stmt = "DELETE FROM aulas_ediciones "                . "WHERE fk_aula = '" . $aula . "' ";        return $this->mysqli->query($stmt);    }    function update($aula, $data) {        if ($this->remove_by($aula)) {            $out = $this->conect($aula, $data);        } else {            $out = "No se pudo ejecutar la operación";        }//        return $out;    }}?>