<?phpclass aulas_profesores extends Database {    function add_profes_to_aula($data) {//        print_r($data);        $stmt = "INSERT INTO aulas_profesores "                . "(fk_aula, fk_profesor) "                . "VALUES "                . "('" . $data["fk_aula"] . "', '" . $data["fk_profesor"] . "');";//        echo $stmt;        return $this->saveRecord($stmt);    }    function update_profes_to_aula($aula, $data) {        if ($data["old_profesor"] != "") {            $stmt = "UPDATE aulas_profesores "                    . "SET fk_profesor = '" . $data["fk_profesor"] . "' "                    . "WHERE fk_aula = '" . $aula . "' "                    . "AND fk_profesor = '" . $data["old_profesor"] . "' ";        } else {            $stmt = "INSERT INTO aulas_profesores "                    . "(fk_aula, fk_profesor) "                    . "VALUES "                    . "('" . $aula . "','" . $data["fk_profesor"] . "')";        }//        echo $stmt;        return $this->mysqli->query($stmt);    }    function remove_profes_aula($aula) {        $stmt = "DELETE FROM aulas_profesores                WHERE fk_aula = '" . $aula . "'";        return $this->mysqli->query($stmt);    }    function get_profes_by_aula($id) {        $stmt = 'SELECT                    fk_profesor                FROM                    aulas_profesores                WHERE                    fk_aula = "' . $id . '"';//        echo $stmt;        $res = $this->getRecord($stmt);        return $res;    }    function get_by_aula($id) {        $stmt = 'SELECT                    fk_profesor,                    fname_people,                    lname1_people,                    email_people                FROM                    aulas_profesores                LEFT JOIN people ON people.id_people = fk_profesor                 WHERE fk_aula = "' . $id . '"'                . 'AND people.fk_type_people = 2';//        echo $stmt;        $res = $this->getRecord($stmt);        return $res;    }    function get_aulas_by_profes($id) {        $stmt = 'SELECT * FROM aulas_profesores                     LEFT JOIN aulas ON aulas.id_aula = aulas_profesores.fk_aula                     WHERE aulas_profesores.fk_profesor = "' . $id . '" '                . 'GROUP BY aulas_profesores.fk_aula ';//        echo $stmt;        $res = $this->getRecord($stmt);        return $res;    }}?>