<?phpclass ediciones extends Database {    function get_all() {        $stmt = 'SELECT                ediciones.id_ediciones,                ediciones.anio_init,                ediciones.mes_init,                servicios.name_servicio,                servicios.id_servicio                FROM ediciones LEFT JOIN servicios ON servicios.id_servicio =  ediciones.fk_servicio                ORDER BY ediciones.id_ediciones DESC';//        echo $stmt;        return $this->getRecord($stmt);    }    function get_by_aula($aula) {        $stmt = 'SELECT                    aulas_ediciones.fk_aula,                    ediciones.anio_init,                    ediciones.mes_init,                    ediciones.fk_servicio,                    servicios.name_servicio                FROM ediciones                LEFT JOIN servicios ON ediciones.fk_servicio = servicios.id_servicio                LEFT JOIN aulas_ediciones ON ediciones.id_ediciones = aulas_ediciones.fk_edicion                WHERE aulas_ediciones.fk_aula = "' . $aula . '"';//        echo $stmt;        return $this->getRecord($stmt);    }    function create_edicion($data) {        $out = true;        $stmt = "SELECT * FROM ediciones";        $data_chk = $this->getRecord($stmt);//        print_r($data_chk);        foreach ($data_chk as $val) {            if (($val['anio_init'] == $data['anio_init']) && ($val['mes_init'] == $data['mes_init']) && ($val['fk_servicio'] == $data['fk_servicio'])) {                $out = false;            }        }        if ($out != false) {            $stmt = "INSERT INTO ediciones"                    . "(anio_init, mes_init,fk_servicio) "                    . "VALUES "                    . "('" . $data["anio_init"] . "', '" . $data["mes_init"] . "', '" . $data["fk_servicio"] . "');";            $out = $this->saveRecord($stmt);        }        return (bool) $out;//        echo $stmt    }    function update_edicion($data, $id) {        $stmt = "UPDATE ediciones "                . "SET anio_init = " . $data["anio_init"] . ", "                . "mes_init =" . $data["mes_init"] . ", "                . "fk_servicio =" . $data["fk_servicio"] . " "                . "WHERE id_ediciones = '" . $id . "'";//        echo $stmt;        return $this->mysqli->query($stmt);    }    function remove_edicion($id) {        $stmt = "DELETE FROM ediciones "                . "WHERE id_ediciones = '" . $id . "'";//        echo $stmt;        return $this->mysqli->query($stmt);    }    function get_edicion_by_id($id) {        $stmt = "SELECT * FROM ediciones WHERE id_ediciones = " . $id . ";";        return $this->getRecord($stmt)[0];    }}