<?phpclass modulos extends Database {    function get_all() {        $stmt = 'SELECT                        aulas.id_aula,                        aulas.nombre_aula,                        aulas.fk_modulo,                        modulos.nombre_modulo,                        modulos.id_modulo,                        aulas.f_inicio,                        aulas.f_fin,                        aulas.url_aula,                        aulas.imagen_aula                FROM                        modulos                LEFT JOIN aulas ON modulos.id_modulo = aulas.fk_modulo                GROUP BY modulos.id_modulo                ORDER BY                aulas.f_inicio ASC';        return $this->getRecord($stmt);    }    function get_by_profe($id) {        $stmt = 'SELECT    aulas.id_aula,    aulas.nombre_aula,    aulas.fk_modulo,    modulos.nombre_modulo,    modulos.id_modulo,    modulos.imagen_modulo as imagen_aula,    aulas.f_inicio,    aulas.f_fin,    aulas.url_aulaFROM    modulosLEFT JOIN aulas ON modulos.id_modulo = aulas.fk_moduloLEFT JOIN aulas_profesores ON aulas_profesores.fk_aula = aulas.id_aulaLEFT JOIN people ON people.id_people = aulas_profesores.fk_profesorWHERE    people.id_people =  ' . $id . 'AND people.fk_type_people = 2GROUP BY    modulos.id_moduloORDER BY    aulas.f_inicio ASC';        return $this->getRecord($stmt);    }    function get_by_id($id) {        $stmt = "SELECT modulos.*, aulas.imagen_aula FROM modulos"                . " LEFT JOIN aulas ON aulas.fk_modulo = modulos.id_modulo"                . " WHERE modulos.id_modulo = '" . $id . "'"                . " AND aulas.imagen_aula != ''"                . " GROUP BY aulas.fk_modulo";        return $this->getRecord($stmt)[0];    }    function get_by_nota($id) {        $stmt = 'SELECT                        modulos.nombre_modulo                FROM                        modulos                LEFT JOIN aulas ON aulas.fk_modulo = modulos.id_modulo                LEFT JOIN aulas_notas ON aulas_notas.fk_aula = aulas.id_aula                WHERE                        aulas_notas.id_aulas_notas = ' . $id;        $res = $this->getRecord($stmt);//        print_r($res);        return "Módulo: " . $res[0]["nombre_modulo"];    }}?>