<?phpfunction get_aulas_ediciones($id_modulo) {    $rol = $_SESSION["user_data"]["id_rols"];    $id_user = $_SESSION["user_data"]["id_user"];    $item_opciones = '<th>URL</th>';    $add_aula = '';    $aulas = new aulas();    $ediciones = new ediciones();//    echo $id_user;    if (($rol == 10) || ($id_user == 100)) {        $aulas_data = $aulas->get_all_aulas();        $add_aula = '<div class="ctm-btn-add">                            <a href="' . BASE_URL . '?nueva_aula" class="btn btn-default btn-success"><i class="icon-plus"></i></a>                        </div> ';    } else {        if ($rol == 2) {            $aulas_data = $aulas->get_aula_by_profe($id_user);        }    }//    print_r($aulas_data);    $aulas_list = '';    $op_url = '';    $dates_ediciones = getYearsMonths();    for ($i = 0; $i < count($aulas_data); $i++) {        $output_ediciones = '';        $ediciones_data = $ediciones->get_by_aula($aulas_data[$i]["id_aula"]);//        print_r($ediciones_data);        if (count($ediciones_data) > 0) {            foreach ($ediciones_data as $item_edicion) {                $output_ediciones .= '<small style="margin:10px;display:block;font-size:11px;">'                         . $item_edicion['name_servicio']                         . '  ( ' . (substr(strtoupper($dates_ediciones['M'][$item_edicion['mes_init'] - 1]), 0, 3))                         . ' - ' . $item_edicion['anio_init'] . ') </small>';            }        }        $aula_photo = get_image_aula($aulas_data[$i]["imagen_aula"]);        if ($rol == 10) {            $item_opciones = '<th class="team-links">Opciones</th>';            $opciones = '<td class="text-center">                            <div class="btn-group">                                <button type="button" class="btn btn-icon btn-success dropdown-toggle" data-toggle="dropdown"><i class="icon-cog4"></i></button>                                    <ul class="dropdown-menu icons-right dropdown-menu-right">                                            <li><a href="' . $aulas_data[$i]["url_aula"] . '" target="_blank"><i class="icon-redo2"></i> Ir al aula virtual</a></li>                                            <li><a href="' . BASE_URL . '?edit_aula=' . $aulas_data[$i]["id_aula"] . '"><i class="icon-quill2"></i> Editar aula</a></li>                                            <li><a data-toggle="modal" data-target="#ctm-rm-aula' . $aulas_data[$i]["id_aula"] . '" ><i class="icon-remove3"></i> Eliminar</a></li>                                    </ul>                            </div>                            <!-- Modal -->                            <div class="modal fade" id="ctm-rm-aula' . $aulas_data[$i]["id_aula"] . '" tabindex="-1" role="dialog">                              <div class="modal-dialog modal-sm">                                <!-- Modal content-->                                <div class="modal-content">                                  <div class="modal-header">                                    <button type="button" class="close" data-dismiss="modal">&times;</button>                                    <h4 class="modal-title">Â¿Desea borrar el aula?</h4>                                  </div>                                  <div class="modal-body">                                  </div>                                  <div class="modal-footer">                                    <button type="button" class="btn btn-default" data-dismiss="modal">Cancel</button>                                  <a class="btn btn-danger btn-ok" href="' . BASE_URL . '?remove_aula=' . $aulas_data[$i]["id_aula"] . '">Delete</a>                                  </div>                                </div>                              </div>                            </div>                        </td>';        } else {            $op_url = '<td class="text-center">                            <a href="' . $aulas_data[$i]["url_aula"] . '" class="tip" data-original-title="Ir al aula" target="_blank"><i class="icon-redo2"></i></a>                        </td>';        }        $aulas_list .= '<tr>                        <td class="text-center">                            <a href="' . $aula_photo . '" class="lightbox" title="' . $aulas_data[$i]["nombre_aula"] . '"><img src="' . $aula_photo . '" alt="" class="img-media"></a>                        </td>                        <td class="text-semibold">                           <a href="' . BASE_URL . '?aula=' . $aulas_data[$i]["id_aula"] . '" title="" class="tip" data-original-title="Ver">' . $aulas_data[$i]["nombre_modulo"] . '</a>                        </td>                        <td class="file-info">                            <span>' . $output_ediciones . '</span>                        </td>                        ' . $op_url . $opciones . '                    </tr>';    }    return '<!-- Page content --><div class="page-content">     <div class="page-content-inner">        <!-- Page header -->        <div class="page-header">            <div class="page-title">                <h3>Lista de aulas<small>Lista detallada de las aulas</small></h3>            </div>              ' . $add_aula . '         </div>        <!-- /page header -->        <!-- Breadcrumbs line -->        <div class="breadcrumb-line">            <ul class="breadcrumb">                    <li><a href="' . BASE_URL . '">Home</a></li>                    <li class="active">Aulas</li>            </ul>        </div>        <!-- /breadcrumbs line -->        <!-- alumnos (table) -->        <div class="block">            <h6 class="heading-hr"><i class="icon-stack"></i>Lista de aulas</h6>            <div class="datatable-media">                <table class="table table-bordered table-striped">                    <thead>                        <tr>