<?phpfunction get_calendars() {    $rol = $_SESSION["user_data"]["id_rols"];    $id_user = $_SESSION["user_data"]["id_user"];    $alert = '';    $calendars = new calendars();    $calendars_data = $calendars->get_all();    $list = '';    $add_alumno = '';    $btn_pass = '';    $btn_file = '';    $rownotas = '';    for ($i = 0; $i < count($calendars_data); $i++) {        $list .= '            <tr>                <td class="text-center">                  <strong><a style="font-size:15px" href="'.BASE_URL.'?config_calendars='.$calendars_data[$i]["id_calendar"].'">'.$calendars_data[$i]["nombre"].'</a></strong>                </td>                <td>                  '.$calendars_data[$i]["fname_people"].'                </td>                <td class="text-center">                    <div class="btn-group">                        <button type="button" class="btn btn-icon btn-info dropdown-toggle" data-toggle="dropdown"><i class="icon-cog4"></i></button>                          <ul class="dropdown-menu icons-right dropdown-menu-right">                              <li><a href="' . BASE_URL . '?config_calendars=' . $calendars_data[$i]["id_calendar"] . '"><i class="icon-user"></i> Ver </a></li>                              <li><a href="' . BASE_URL . '?edit_calendars=' . $calendars_data[$i]["id_calendar"] . '"><i class="icon-quill2"></i> Editar </a></li>                              <li><a data-toggle="modal" data-target="#ctm-rm-aula' . $calendars_data[$i]["id_calendar"] . '" ><i class="icon-remove3"></i> Eliminar</a></li>                          </ul>                    </div>                    <!-- Modal -->                    <div class="modal fade" tabindex="-1" id="ctm-rm-aula' . $calendars_data[$i]["id_calendar"] . '" role="dialog">                      <div class="modal-dialog modal-sm">                        <!-- Modal content-->                        <div class="modal-content">                          <div class="modal-header">                            <button type="button" class="close" data-dismiss="modal">&times;</button>                            <h4 class="modal-title">¿Desea borrar el alumno?</h4>                          </div>                          <div class="modal-body">                          </div>                          <div class="modal-footer">                            <button type="button" class="btn btn-default" data-dismiss="modal">Cancel</button>                          <a class="btn btn-danger btn-ok" href="' . BASE_URL . '?remove_calendars=' . $calendars_data[$i]["id_calendar"] . '">Delete</a>                          </div>                        </div>                      </div>                    </div>                </td>            </tr>';    }    $add_alumno = '<div class="ctm-btn-add">                    <a href="' . BASE_URL . '?nuevo_calendars" class="btn btn-default btn-success"><i class="icon-user-plus2"></i></a>                    </div>';    if (isset($_GET["ok"])) {        $alert = '<div class="callout callout-success fade in">				<button type="button" class="close" data-dismiss="alert">×</button>				<h5>Operación exitosa</h5>			</div>';    }    return '<!-- Page content --><div class="page-content">    <div class="page-content-inner">        <!-- Page header -->        <div class="page-header">            <div class="page-title">                <h3>Lista de calendarios<small>Lista detallada de calendarios</small></h3>            </div>            ' . $add_alumno . '        </div>        <!-- /page header -->        <!-- Breadcrumbs line -->        <div class="breadcrumb-line">            <ul class="breadcrumb">                    <li><a href="' . BASE_URL . '">Home</a></li>                    <li class="active">Lista de calendarios</li>            </ul>        </div>        <!-- /breadcrumbs line -->        ' . $alert . '        <!-- alumnos (table) -->        <div class="block">  