<?phpfunction get_ediciones() {    $alert = "";    if (isset($_GET["ok"])) {        $alert = '<div class="callout callout-success fade in">				<button type="button" class="close" data-dismiss="alert">×</button>				<h5>Operación exitosa</h5>							</div>';    } else {        if (isset($_GET["ko"])) {            $alert = '<div class="callout callout-danger fade in">				<button type="button" class="close" data-dismiss="alert">×</button>				<h5>La operación ha fallado, compruebe si los cambios son correctos</h5>							</div>';        }    }    $ediciones = new ediciones();    $ediciones_data = $ediciones->get_all();//    print_r($ediciones_data);    $list_ediciones = '';    $dates_editions = getYearsMonths();//    print_r($dates_editions);    foreach ($ediciones_data as $k => $v) {        $list_ediciones .= '<tr>                        <td class="text-semibold"> <strong>( ' . $v['anio_init'] . ' - ' . substr(strtoupper($dates_editions['M'][$v['mes_init'] - 1]),0,3) . ' ) </strong>' . $v["name_servicio"] . '</td><!--                        <td class="text-center">                            <span>' . $v['anio_init'] . ' - ' . strtoupper($dates_editions['M'][$v['mes_init'] - 1]) . '</span>                        </td>-->                        <td class="text-center">                            <div class="btn-group">                                <button type="button" class="btn btn-icon btn-info dropdown-toggle" data-toggle="dropdown"><i class="icon-cog4"></i></button>                                    <ul class="dropdown-menu icons-right dropdown-menu-right">                                            <li><a href="' . BASE_URL . '?edit_edicion=' . $v["id_ediciones"] . '"><i class="icon-pencil"></i> Editar</a></li>                                            <li><a data-toggle="modal" data-target="#ctm-rm-aula' . $v["id_ediciones"] . '" ><i class="icon-remove3"></i> Eliminar</a></li>                        