line">            <ul class="breadcrumb">                <li><a href="' . BASE_URL . '">Home</a></li>                <li><a href="' . BASE_URL . '?servicios">Servicios</a></li>                <li class="active">Ediciones</li>            </ul>        </div>        <!-- /breadcrumbs line -->        ' . $alert . '        <!-- profesores (table) -->        <div class="block">            <div class="datatable-media">                <table class="table table-bordered table-striped">                    <thead>                        <tr>                            <th>Servicio</th>                            <!--<th class="team-links">Edicion</th>-->                            <th class="team-links">Opciones</th>                        </tr>                    </thead>                    <tbody>                        ' . $list_ediciones . '                    </tbody>                </table>            </div>        </div>        <!-- /profesores (table) -->    </div>    ' . get_footer() . '</div>	<!-- /page container -->';}