<?phpfunction get_notas_by_alumno($id) {//echo ($_SESSION["user_data"]["id_rols"] == 10);    $alumno = new people();    $profile_data = $alumno->get_info_by_id($id);//    print_r($profile_data);    $notas_alumnos = new notas_alumnos();    $notas_alumnos_data = $notas_alumnos->get_notas_by_alumno($id);//    print_r($aula_data);    $data_notas_details = $notas_alumnos->get_notas_details_by_aula($id);//    print_r($data_notas_details);    $data_notas = array();    $anterior = 0;    $out_notas = '';    if (is_array($notas_alumnos_data) && (count($notas_alumnos_data) > 0)) {        $out_notas = "";        $notas_ordenadas = array();        $e = 0;        foreach ($notas_alumnos_data as $v) {            $notas_ordenadas[$v['fk_modulo']][$e] = $v;            $e++;        }//        print_r($notas_ordenadas);        foreach ($notas_ordenadas as $v) {            $notas_list = "";            if (count($v) > 0) {//                print_r($v);                foreach ($v as $vv) {                    $notas_list .= '<tr>';                    $notas_list .= '<td>' . $vv['title_aulas_notas'] . '</td>';                    $notas_list .= '<td>' . $vv['nota_alumno'] . '</td>';                    $notas_list .= '<td>' . $vv['comentario'] . '</td>';                    $notas_list .= '</tr>';                }            }            $out_notas .= '<div class="">                            <table class="table table-bordered">                                <thead>                                    <tr>                                        <th style="background:#ccc;" colspan="3">' . $vv["nombre_modulo"] . '</th>                                    </tr>                                    <tr>                                        <th style="width:30%;">Nombre</th>                                        <th style="width:10%;">Nota</th>                                        <th>comentario</th>                                    </tr>                                </thead>                                <tbody>                                    ' . $notas_list . '                                </tbody>                            </table>                            </div>';        }    }        $notas_list = '';    $list_notas_prev = '';    if (is_array($data_notas)) {        $salida = '<tr><th>Nota</th>';        for ($e = 0; $e < count($data_notas_details); $e++) {            $salida .= "<th>" . $data_notas_details[$e]["title_aulas_notas"] . "</th>";        }        $salida .= '</tr>';        foreach ($data_notas as $k => $v) {            $list_notas_prev = '';            if (is_array($v["notas"])) {                foreach ($data_notas_details as $val) {                    $list_notas_prev .= '<td data-original-title="' . $v["notas"][$val["id_aulas_notas"]]["commment"] . '">' . $v["notas"][$val["id_aulas_notas"]]["nota"] . '</td>';                }            }            $notas_list .= '<tr>                                <td>' . $v["name"] . '<span style="display:block;font-weight:bold">' . $v["email"] . '</span></td>                                ' . $list_notas_prev . '                            </tr>';        }    }    return '<!-- Page content --><div class="page-content">     <div class="page-content-inner">        <!-- Page header -->        <div class="page-header">            <div class="page-title">                <h3>' . $profile_data["full_name"] . '<small>Resumen de notas</small></h3>            </div>          </div>        <!-- /page header -->        <!-- alumnos (table) -->        <div class="row">            <div class="col-xs-12">                <div class="panel panel-default">                    <div class="panel-heading"><h6 class="panel-title"><i class="icon-table2"></i> Notas ' . $profile_data['full_name'] . '</h6><a id="btn_pass" href="' . BASE_URL . '?notas_alumno_excel=' . $id . '" target="_blank" class="tip btn btn-success" data-original-title="Descargar Excel"><i class="icon-file"></i></a>                        </div>                    </div>                       ' . $out_notas . '                </div>            </div>        </div>        ' . get_footer() . '    </div></div>	<!-- /page container -->';}