<?phpfunction get_notas_aula_resumen($id) {//echo ($_SESSION["user_data"]["id_rols"] == 10);    $alumnos_notas = new notas_alumnos();    $aulas = new aulas();    $aula_data = $aulas->get_aula_by_id($id);//    print_r($aula_data);    $data_notas_details = $alumnos_notas->get_notas_details_by_aula($id);//    print_r($data_notas_details);     $data_notas_alumnos = $alumnos_notas->get_all_notas_by_aula($id);    $data_notas = array();    for ($i = 0; $i < count($data_notas_alumnos); $i++) {        $data_notas[$data_notas_alumnos[$i]['id_people']]['name'] = $data_notas_alumnos[$i]['fname_people'] . " " . $data_notas_alumnos[$i]['lname1_people'] . " " . $data_notas_alumnos[$i]['lname2_people'];        $data_notas[$data_notas_alumnos[$i]['id_people']]['email'] = $data_notas_alumnos[$i]['email_people'];        $data_notas[$data_notas_alumnos[$i]['id_people']]['notas'][$data_notas_alumnos[$i]['id_aulas_notas']]['title'] = $data_notas_alumnos[$i]['title_aulas_notas'];        $data_notas[$data_notas_alumnos[$i]['id_people']]['notas'][$data_notas_alumnos[$i]['id_aulas_notas']]['nota'] = $data_notas_alumnos[$i]['nota_alumno'];        $data_notas[$data_notas_alumnos[$i]['id_people']]['notas'][$data_notas_alumnos[$i]['id_aulas_notas']]['commment'] = $data_notas_alumnos[$i]['comentario'];    }    $notas_list = '';    $list_notas_prev = '';    if (is_array($data_notas)) {        $salida = '<tr><th>Alumno</th>';        for ($e = 0; $e < count($data_notas_details); $e++) {            $salida .= "<th>" . $data_notas_details[$e]["title_aulas_notas"] . "</th>";        }        $salida .= '</tr>';        foreach ($data_notas as $k => $v) {            $list_notas_prev = '';            if (is_array($v["notas"])) {                foreach ($data_notas_details as $val) {                    $list_notas_prev .= '<td data-original-title="' . $v["notas"][$val["id_aulas_notas"]]["commment"] . '">' . $v["notas"][$val["id_aulas_notas"]]["nota"] . '</td>';                }            }            $notas_list .= '<tr>                                <td>' . $v["name"] . '<span style="display:block;font-weight:bold">'.$v["email"].'</span></td>                                ' . $list_notas_prev . '                            </tr>';        }    }    return '<!-- Page content --><div class="page-content">     <div class="page-content-inner">        <!-- Page header -->        <div class="page-header">            <div class="page-title">                <h3>Resumen<small>Todas las notas del aula</small></h3>            </div>          </div>        <!-- /page header -->        <!-- Breadcrumbs line -->        <div class="breadcrumb-line">                <ul class="breadcrumb">                        <li><a href="' . BASE_URL . '">Home</a></li>                        <li><a href="' . BASE_URL . '?lista_aulas">Aulas</a></li>                        <li><a href="' . BASE_URL . '?aula=' . $id . '">' . $aula_data['nombre_aula'] . '</a></li>                        <li class="active">Notas</li>                </ul>        </div>        <!-- /breadcrumbs line -->        <!-- alumnos (table) -->        <div class="row">            <div class="col-xs-12">                <div class="panel panel-default">                    <div class="panel-heading"><h6 class="panel-title"><i class="icon-table2"></i> Notas ' . $aula_data['nombre_aula'] . '</h6><a id="btn_pass" href="' . BASE_URL . '?notas_excel=' . $id . '" target="_blank" class="tip btn btn-success" data-original-title="Descargar Excel"><i class="icon-file"></i></a>                        </div>                        <div class="table-responsive">                                <table class="table table-bordered">                                    <thead>                                        <tr>                                           ' . $salida . '                                        </tr>                                    </thead>                                    <tbody>                                        ' . $notas_list . '                                    </tbody>                                </table>                        </div>                    </div>                </div>            </div>        </div>        ' . get_footer() . '    </div></div>	<!-- /page container -->';}